mapper.select:
select *
from tb_doctor_info

    login_name=#{loginName,jdbcType=VARCHAR}
    and password=#{password,jdbcType=VARCHAR}

mapper.update:
update tb_doctor_info
set
password = #{newPassword,jdbcType=VARCHAR},
salt = #{salt,jdbcType=VARCHAR}
where
login_name = #{loginName,jdbcType=VARCHAR}
