mapper.update:
update tb_examination_paper
set judge_status = "1"
where examination_paper_id = #{examinationPaperId,jdbcType=VARCHAR}

mapper.select:
select tep.examination_paper_id as examinationPaperId, tr.report_id as reportId, tr.report_name as reportName, tpi.patient_name as patientName, tep.create_time as createTime from tb_examination_paper tep left join tb_patient tpi on tep.patient_id = tpi.patient_id left join tb_report tr on tep.report_id = tr.report_id
tr.report_name like concat('%', #{reportName}, '%')
and tpi.patient_name = #{patientName,jdbcType=VARCHAR}
and tpi.doctor_id = #{doctorId,jdbcType=VARCHAR}
order by tep.create_time desc

select tr.report_name as reportName
from tb_examination_paper tep left join tb_report tr on tr.report_id = tep.report_id
where tep.examination_paper_id = #{examinationPaperId,jdbcType=VARCHAR}
