mapper.update:
update tb_examination_paper
set judge_status = "1"
where examination_paper_id = #{examinationPaperId,jdbcType=VARCHAR}

mapper.select:
select * from tb_examination_paper tep
left join tb_patient_info tpi on tep.patient_id = tpi.patient_id
left join tb_scale_info tsi on tep.scale_id = tsi.scale_id


        tsi.scale_name like concat('%', #{scaleName}, '%')


        and tpi.patient_name = #{patientName,jdbcType=VARCHAR}


        and judge_status = #{judgeStatus,jdbcType=VARCHAR}

    and tpi.doctor_id = #{doctorId,jdbcType=VARCHAR}

order by tep.create_time desc

select * from tb_examination_paper tep
left join tb_patient_info tpi on tep.patient_id = tpi.patient_id

    and tep.examination_paper_id=#{examinationPaperId,jdbcType=VARCHAR}
    and tpi.doctor_id = #{doctorId,jdbcType=VARCHAR}

select tep.examination_paper_id, tsi.scale_name, tpi.patient_name, tep.use_time, tep.judge_status, tji.total_score from tb_examination_paper tep left join tb_patient_info tpi on tep.patient_id = tpi.patient_id left join tb_scale_info tsi on tep.scale_id = tsi.scale_id left join tb_judge_info tji on tep.examination_paper_id = tji.examination_paper_id where examination_paper_id in
#{examinationPaperId,jdbcType=VARCHAR}
and doctor_id=#{doctorId,jdbcType=VARCHAR}
